#include <cstdio>
#include <vector>
using namespace std;
typedef vector<int> VI;
typedef vector<VI> VVI;
typedef long long LL;

#define FOR(i,a,b) for(int i=a;i<int(b);i++)
#define SZ(a) ((int)(a).size())
#define MOD 998244353

struct RS {
	int wp; // with parent, take
	int nt; // with parent, do not take
	int np; // without parent
};

int N;
VVI adj;
vector<RS> rss;

void dfs(int n, int p) {
	VI& aj = adj[n];
	if (p != -1) aj.erase(find(aj.begin(), aj.end(), p));
	RS& rs = rss[n];
	if (aj.empty()) {
		rs.wp = 1;
		rs.nt = 0;
		rs.np = 1;
		return;
	}
	for (int m : aj) dfs(m, n);
	VI pfx(aj.size()), sfx(aj.size());
	pfx[0] = 1;
	FOR(i,1,aj.size()) {
		RS const& rsm = rss[aj[i-1]];
		int x = rsm.nt + rsm.np;
		x %= MOD;
		pfx[i] = int((LL(pfx[i-1])*x)%MOD);
	}
	sfx[SZ(aj)-1] = 1;
	for (int i = SZ(aj)-2; i >= 0; i--) {
		RS const& rsm = rss[aj[i+1]];
		int x = rsm.nt + rsm.np;
		x %= MOD;
		sfx[i] = int((LL(sfx[i+1])*x)%MOD);
	}
	rs.wp = 1;
	rs.nt = 0;
	LL allnp = 1;
	FOR(i,0,SZ(aj)) {
		RS const& rsm = rss[aj[i]];
		int x = rsm.nt + rsm.np;
		rs.wp = int((LL(rs.wp) * x)%MOD);
		LL nt = rsm.wp;
		nt = (nt * pfx[i]) % MOD;
		nt = (nt * sfx[i]) % MOD;
		rs.nt = int((rs.nt + nt) % MOD);
		allnp = (allnp * rsm.np) % MOD;
	}
	rs.np = (rs.nt + allnp) % MOD;
	//printf("Called %d wp=%d nt=%d np=%d\n", n+1, rs.wp, rs.nt, rs.np);
}

int main() {
	scanf("%d", &N);
	adj.resize(N);
	FOR(a,0,N-1) {
		int u, v;
		scanf("%d%d", &u, &v); --u; --v;
		adj[u].push_back(v);
		adj[v].push_back(u);
	}
	rss.resize(N);
	dfs(0, -1);
	printf("%d\n", rss[0].np);
}

#include <bits/stdc++.h>
using namespace std;
vector <vector <long long> > G;
vector <long long> stopnie;
vector <vector <long long> > stany; 
long long modulo = 998244353;
/*
stan 1 - jesli jest z synkiem LUB izolowany ( i kazde poddrzewo jest ok)
stan 2 - laczymy z tata, nie z jakims synkiem
stan 3 - laczy sie z ktoryms z synkow
*/
long long SzypkiePot(long long pod, long long pot, long long mod){
    if (pot == 0){return 1;}
    long long wynik = SzypkiePot(pod, pot/2, mod)%mod;
    if (pot % 2 == 0){ return (wynik*wynik)%mod;}
    else{  return (pod*((wynik*wynik)%mod))%mod; }
}
long long Odw(long long x, long long mod){ return SzypkiePot(x, mod-2, mod)%mod;}


void Licz(long long v, long long tata){
    cerr << "v  "<<v << "\n";
    long long stan2 = 1; //czyli polacze z tata
    for (auto w : G[v]){ // dla kazdego synka chce zeby albo sie izolowal albo laczyl z synem
        if (w == tata) {continue;} // jesli nie jest synem
        stan2 = ((stany[w][0] + stany[w][2])*stan2)%modulo; // domnazam na ile sposobow 
        // cerr << "Domnazam "<<stany[w][0] + stany[w][2] << "\n";
         // moj synek moze albo sie laczyc z synem swoim albo byc (izolowany lub z synem)
    }
    long long stan3 = 0; // polacze z synkiem
    for (auto w : G[v]){ // wybieram syna do polaczenia
        if (w == tata){continue;}
        long long odwrotnosc_mod = Odw((stany[w][0] + stany[w][2]), modulo);
        // pozostalych laczymy jakos albo z synkami albo opcja (izoluj/synkowie)
        // cerr << "odwrotnosc " << odwrotnosc_mod << "\n";
        if (odwrotnosc_mod < 0){
            odwrotnosc_mod = (odwrotnosc_mod + modulo) % modulo;
        }
        long long sposoby_pozostalych = (stan2 * odwrotnosc_mod)%modulo; // wszystkie sposoby bez tego dolaczonego
        // cerr << "sposoby pozostalych "<<sposoby_pozostalych << "\n";
        long long polacz = (stany[w][1]*sposoby_pozostalych)%modulo; // tego w lacze z tata
        if (polacz < 0){
            polacz = (polacz + modulo)%modulo;
        }
        // cerr << "polacz = "<<polacz << "\n";
        stan3 = (stan3 + polacz)%modulo;
    }
    long long stan1 = 1;
    for (auto w : G[v]){
        if (w == tata){continue;}
        stan1 = (stan1 * stany[w][0])%modulo;
    }
    stan1 = (stan1 + stan3)%modulo;
    // cerr << "stany " << stan1 << " " << stan2 << " " << stan3 << "\n";
    stany[v]={stan1, stan2, stan3};
}
void DFS(long long v, long long tata){
    // cerr << "v "<<v << " tata " << tata << "\n";
    for (auto w : G[v]){
        if (w != tata){
            DFS(w, v);
        }
    }
    Licz(v, tata);
}
int main(){
    ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);
    long long n;
    cin >> n;
