mint ma[300005], un[300005];
mint gore[300005], dole[300005];

void dfs1(int x, int p) {
	un[x] = 1;
	gore[x] = 1;

	basic_string<mint> z, z_pref, z_suff, gore1;

	for (int y : e[x]) {
		if (y == p)
			continue;
		dfs1(y, x);
		un[x] *= dole[y] + un[y];
		gore[x] *= dole[y] * 2 + un[y]; 
		// dole
		z += un[y] + dole[y];
		gore1 += gore[y];
	}

	z_pref = z;
	z_suff = z;
	int m = z.size();
	if (m > 0) {
		z_pref[0] = 1;
		for (int i=0; i<m-1; i++)
			z_pref[i+1] = z_pref[i] * z[i];
		z_suff[m-1] = 1;
		for (int i=m-1; i>0; i--)
			z_suff[i-1] = z_suff[i] * z[i];
		for (int i=0; i<m; i++)
			dole[x] += gore1[i] * z_pref[i] * z_suff[i];
	}
}

void dfs0(int x, int p) {
	un[x] = 1;
	ma[x] = 0;

	basic_string<mint> z, z_pref, z_suff, un1;

	for (int y : e[x]) {
		if (y == p)
			continue;
		dfs0(y, x);
		un[x] *= ma[y] + un[y];
		z += ma[y] + un[y];
		un1 += un[y];
	}
	z_pref = z;
	z_suff = z;
	int m = z.size();
	if (m > 0) {
		z_pref[0] = 1;
		for (int i=0; i<m-1; i++)
			z_pref[i+1] = z_pref[i] * z[i];
		z_suff[m-1] = 1;
		for (int i=m-1; i>0; i--)
			z_suff[i-1] = z_suff[i] * z[i];
		for (int i=0; i<m; i++)
			ma[x] += un1[i] * z_pref[i] * z_suff[i];
	}

}

