vector<int> f(12);

int fact(int n){
  if(n==1) return 1;
  if(f[n] != -1) return f[n];
  f[n] = fact(n-1)*n;
  return f[n];
}

int main(){
  for(int i=0; i<f.size(); ++i)
    f[i] = -1;

  int N;
  cin >> N;

  long long num;
  cin >> num;

  long long prod = 1;

  for(int i=0; i<N; ++i){
    int digit = num % 10;
    prod *= fact(digit);
    num /= 10;
  }

  vector<int> digits;
  while(prod%7==0){
    prod /= fact(7);
    digits.push_back(7);
  }

  while(prod%5==0){
    prod /= fact(5);
    digits.push_back(5);
  }

  while(prod%3==0){
    prod /= fact(3);
    digits.push_back(3);
  }
  while(prod%2==0){
    prod /= 2;
    digits.push_back(2);
  }



  sort(digits.begin(), digits.end());

  for(int i=digits.size()-1; i>=0; --i)
    cout << digits[i];
  cout << endl;

}

