long bs(vector<unsigned long long>a, unsigned long long x) {
long lo = 0,
hi = a.size() - 1;
while (lo <= hi) {
long m = (lo + hi) / 2;
if (a[m] == x) {
return m+1;
} else if (x < a[m]) {
hi = m - 1;
} else if (x > a[m]) {
lo = m + 1;
}
}
return 0;
}
