        (t == '1') ? p++ : r++;
    }

    int lo = 1, hi = p;
    while (lo < hi) {
        int p1 = (lo+hi)/3, p2 = 2*(lo+hi)/3;
        if (p1 < lo) p1 = lo;
        int s1 = solve(p1), s2 = solve(p2);
        if (s1 == s2) lo = p1, hi = p2;
        else if (s1 > s2) lo = p1+1;
        else hi = p2-1;
    }
    cout << solve(lo) << endl;
}

