const ll mod   = 998244353ll;
const int maxn = 300005;

vector<int> E[maxn];

int n;

ll dp[maxn][3];

// dp[i][0] = sum(max(dp[sub[i]][0/1]))
// dp[i][1] 

void addedge(int u, int v) {
  E[u].pb(v);
  E[v].pb(u);
}

void dfs(int u, int fa=-1) {
  dp[u][0] = 1LL;
  dp[u][1] = 1LL;
  dp[u][2] = 0;
  for(auto v : E[u]) {
    if(v == fa) continue;
    dfs(v, u);
    dp[u][1] = (dp[u][1] * ( (dp[v][0] + dp[v][2]) % mod ) % mod;
    dp[u][0] = (dp[u][0] * dp[v][0]) % mod;
  }
  for(auto v : E[u]) {
    if(v == fa) continue;
    dp[u][2] = (dp[u][2] + dp[v][1] * dp[u][1] % mod * rev(dp[v][0] + dp[v][2], mod) % mod) % mod;
  }
  dp[u][0] = (dp[u][0] + dp[u][2]) % mod;
}

void solve() {
  dfs(1);
  ll ans = dp[1][0];
  cout<<ans<<endl;
}

