inline void AddE(int from,int to) {
	bus[++z].next = st[from];
	st[from] = z;
	bus[z].to = to;
}
inline void dfs(int now) {
	f[now][0] = 0;// 
	f[now][1] = 0;// 
	f[now][2] = 1;// 
	for (int i = st[now];i;i = bus[i].next) {
		int s = bus[i].to;
		if (s == fa[now])
			continue;
		fa[s] = now;
		dfs(s);
		f[now][1] = (f[now][1]*(f[s][2]+f[s][1]*2)%mod+(f[now][2]+f[now][0])*(f[s][0]+f[s][2]))%mod;
		f[now][0] = (f[now][0]*(f[s][2]+f[s][1]*2)%mod+f[now][2]*f[s][1])%mod;
		f[now][2] = f[now][2]*(f[s][2]+f[s][1])%mod; 
	}
//	cout << now << " " << f[now][0] << " " << f[now][1] << " " << f[now][2] << endl;
}
