    {
        if(sum1==k&&sum2==k-1)
        tot++;
        return;
    }
    for(int i=1;i<=4;i++)
        if(i!=ans[dep-1])
        {
            ans[dep]=i;
            if(dep>=3)
            {
                if(ans[dep-2]<ans[dep-1]&&ans[dep]<ans[dep-1])
                    sum1++;
