void init()
{
    memset(matrix.m,0,sizeof(matrix.m));
    FOR(i,1,4) matrix.m[5][i]=1;  matrix.m[5][5]=1;
    FOR(i,1,4) matrix.m[1][i]=1;  matrix.m[1][1]=2;
    FOR(i,1,4) matrix.m[2][i]=1;  matrix.m[2][2]=2;
    matrix.m[3][1]=1;  matrix.m[3][3]=1;
    matrix.m[4][2]=1;  matrix.m[4][4]=1;
    matrix.m[6][6]=1;  matrix.m[6][5]=1;
}

board tich(board a, board b)
{
    memset(c.m,0,sizeof(c.m));
    FOR(i,1,6) FOR(j,1,6)
        FOR(t,1,6) c.m[i][j]=(c.m[i][j]+a.m[i][t]*b.m[t][j])%n;
    return c;
}

long long get(long long x, long long y)
{
    return (x >> y) & 1;
}

void solve()
{
    int p;
    FOR(i,0,61) if (get(t,i)) p=i;
    memset(s.m,0,sizeof(s.m));
    FOR(i,1,6) s.m[i][i]=1;
    DFU(i,p,0) {
        s=tich(s,s);
        if (get(t,i)) s=tich(s,matrix);
    }
}

