struct trie{
    LL ara[100][2],sz;
    bool isSuffix[100];
    trie(){}
    trie(LL _sz){
        sz=_sz;
        mem(ara,-1);
        mem(isSuffix,false);
    }
    void insrt(LL root,char text[],LL pos){
        if(text[pos]=='\0'){isSuffix[root]=true;return;}
        LL bit=text[pos]-'0';
        if(ara[root][bit]!=-1){
            insrt(ara[root][bit],text,pos+1);
        }
        else{
            sz++;
            ara[root][bit]=sz-1;
            insrt(ara[root][bit],text,pos+1);
        }
    }
    void print(LL root){
        if(ara[root][0]!=-1){
            pll(ara[root][0],0LL);NL;
            print(ara[root][0]);
        }
        if(ara[root][1]!=-1){
            pll(ara[root][1],1LL);NL;
            print(ara[root][1]);
        }
    }
