LL re(LL pos,LL maxMatch,LL suffix,LL st,LL sufMask,bool f,bool done){
    LL i,j,k,nxt,nwSuffix,nwSufMask;bool fChk,doneChk;
    fChk=doneChk=false;
    k=maxMatch-1;
    if(pos==n){
//        pi(f);NL;
        if(f){return 1;}
//        pll(sufMask,k+1);NL;
        while(k+1!=0){
            if(sufMask&(1LL<<(len-k-1))){return 1;}
            k=prefix[k]-1;
        }
        return 0;
    }
    if(dp[pos][maxMatch][st][f][done]!=-1){return dp[pos][maxMatch][st][f][done];}
    while(k>=0&&str[k+1]!='0'){k=prefix[k]-1;}
    if(str[k+1]=='0'){k++;}
    if(k+1==len){k=prefix[k]-1;fChk=true;}
    if(done){
        doneChk=done;
        nwSuffix=suffix;
        nxt=st;
        nwSufMask=sufMask;
    }
    else{
        if(T.ara[st][0]!=-1){
            nxt=T.ara[st][0];
            if(T.isSuffix[nxt]){nwSuffix=pos+1;}
            else{nwSuffix=suffix;}
            nwSufMask=sufMask|(1LL<<nwSuffix);
            doneChk=done;
        }
        else{
            doneChk=true;
            nwSuffix=suffix;
            nxt=st;
            nwSufMask=sufMask;
        }
    }
    LL s1=re(pos+1,k+1,nwSuffix,nxt,nwSufMask,f|fChk,done|doneChk);
    fChk=doneChk=false;
    k=maxMatch-1;
    while(k>=0&&str[k+1]!='1'){k=prefix[k]-1;}
    if(str[k+1]=='1'){k++;}
    if(k+1==len){k=prefix[k]-1;fChk=true;}
    if(done){
        doneChk=done;
        nwSuffix=suffix;
        nxt=st;
        nwSufMask=sufMask;
    }
    else{
        if(T.ara[st][1]!=-1){
            nxt=T.ara[st][1];
            if(T.isSuffix[nxt]){nwSuffix=pos+1;}
            else{nwSuffix=suffix;}
            nwSufMask=sufMask|(1LL<<nwSuffix);
            doneChk=done;
        }
        else{
            doneChk=true;
            nwSuffix=suffix;
            nxt=st;
            nwSufMask=sufMask;
        }
    }
    LL s2=re(pos+1,k+1,nwSuffix,nxt,nwSufMask,f|fChk,done|doneChk);
    return dp[pos][maxMatch][st][f][done]=s1+s2;
}
