#include <iostream>

using namespace std;
int matk , mdef , mhp, atk , def , hp , a , b , c;

bool can_win( int a1 , int b1 , int c1 )
{
    int ma=mhp,mb=matk,mc=mdef;
    b1-=mc;
    mb-=c1;
    if(mb<=0&&b1<=0)
        return 0;
    while(a1>0&&ma>0)
    {
        a1-=mb;
        ma-=b1;
    }
    if(a1<=0)
        return 0;
    else
        return 1;
}
const int N=400;
long long dp[N][N][N];

int main()
{
    cin>>hp>>atk>>def;
    cin>>mhp>>matk>>mdef;
    cin>>a>>b>>c;
    long long answer=1e9;
    dp[hp][atk][def]=0;
    if(can_win(hp,atk,def))
    {
        cout<<0<<endl;
        return 0;
    }
    for(int i=hp;i<N;i++)
    {
        if(i!=hp)
            dp[i][atk][def]=dp[i-1][atk][def]+a;
        for(int j=atk;j<N;j++)
        {
            if(j!=atk)
                dp[i][j][def]=dp[i][j-1][def]+b;
            for(int k=def;k<N;k++)
            {
                if(k!=def)
                    dp[i][j][k]=dp[i][j][k-1]+c;
                if(can_win(i,j,k))
                {
                    if(answer>dp[i][j][k])
                        {
                            answer=dp[i][j][k];
                        }

                }
            }
        }
    }
    cout<<answer<<endl;
    return 0;
}

