int sumMin(int n, int m, int a, int b)
{
    if ( n == 0 )
        return 0;

    int sol1 = a + sumMin(n-1, m, a, b);
    int sol2 = b + sumMin(n-m, m, a, b);
    return min(sol1, sol2);
}

