using namespace std;
#define N 105
struct node
{
    __int64 x,cnt;
    char c[20];
    friend bool operator<(node a,node b)
    {
        return a.x>b.x;
    }
};
__int64 bfs(char *s,int k)
{
    queue<node >q;
    node cur,next;
    int len=strlen(s),i,j;
    char ch;
    __int64 max=0;
    strcpy(cur.c,s);
    cur.cnt=k;
    cur.x=0;
    for(i=0;i<len;i++)
    {
        cur.x*=10;
        cur.x+=s[i]-'0';
    }
    q.push(cur);
    while(!q.empty())
    {
        cur=q.front();
        q.pop();
        if(cur.cnt<0)
            continue;
        if(cur.x>max)
            max=cur.x;
        for(i=0;i<len-1;i++)
        {
            if(cur.c[i]>=cur.c[i+1])
