ll x, sum;
map< ll, ll > dp[45];

ll rec( int i ) {
    if( sum == 0 ) {
        if( x != 0 ) return 0;
        return 1;
    }
    if( sum < 0 || (1LL<<i) > sum ) return 0;

    if( dp[i][sum] != 0 ) return dp[i][sum] - 1;
    ll temp = (1LL<<i);

    if( temp & x ) {
        x -= temp;
        sum -= temp;
        ll w = rec(i+1);
        sum += temp;
        dp[i][sum] = (w*2) + 1;
        x += temp;
        return dp[i][sum] - 1;
    } else {
        ll w = rec(i + 1);
        sum -= (temp*2);
        w += rec(i + 1);
        sum += (temp*2);
        dp[i][sum] = w+1;
        return w;
    }
}

