void bfs(int start) {
    dist[start] = 0;
    queue < int > q;
    q.push(start);
    while (!q.empty()) {
        int v = q.front();
        q.pop();
        for (auto u : rev_gr[v]) {
            if (dist[u] < dist[v] + 1) {
                continue;
            }
            dist[u] = dist[v] + 1;
            q.push(u);
        }
    }
}

