#define int long long 
using namespace std;


int n, m;


int dp[1 << 6][1 << 6][1 << 6][6][40];


int f(int mask1, int mask2, int mask3, int idx, int idy) {
	if(idy < 0) return 0;
	if(idx < 0) {
		return f(mask2, mask3, 0, m - 1, idy - 1);
	}
	if(~dp[mask1][mask2][mask3][idx][idy]) return dp[mask1][mask2][mask3][idx][idy];

	if((mask2 >> idx) & 1) {
		return 1 + f(mask1, mask2, mask3, idx - 1, idy);
	} 
	int res = 1 + f(mask1, mask2 | (1 << idx), mask3, idx - 1, idy);

	if(idy < n - 1) res = min(res, ((mask1 >> idx) & 1) + f(mask1 | (1 << idx), mask2, mask3, idx - 1, idy));

	if(idx > 0) res = min(res, ((mask2 >> (idx - 1)) & 1) + f(mask1, mask2 | (1 << (idx - 1)), mask3, idx - 1, idy));

	if(idx < m - 1) res = min(res, ((mask2 >> (idx + 1)) & 1) + f(mask1, mask2 | (1 << (idx + 1)), mask3, idx - 1, idy));

	if(idy > 0) res = min(res, f(mask1, mask2, mask3 | (1 << idx), idx - 1, idy));

	return dp[mask1][mask2][mask3][idx][idy] = res;
}


