const int maxn = 15;
const int mod = 1000000000;

int a[maxn][maxn];
int now[maxn][maxn];
int n, m, num;
int ans;
LL goal;

void dfs(int x, int y, LL s, int step)
{
    if (step >= ans)
        return ;
    if (s == goal)
    {
        ans = min(ans, step);
        return ;
    }
    if (y == m)
    {
        dfs(x + 1, 0, s, step);
        return ;
    }
    if (x == n)
        return ;
    if (now[x][y] != -1)
    {
        dfs(x, y + 1, s, step);
        return ;
    }
    for (int i = 0; i < num; i++)
    {
        if (s & (1LL << i))
            continue;
        if (y + 1 < m && now[x][y+1] == -1)
        {
            now[x][y] = now[x][y+1] = i;
            dfs(x, y + 1, s | (1LL << i), step + (now[x][y] != a[x][y]) + (now[x][y+1] != a[x][y+1]));
            now[x][y] = now[x][y+1] = -1;
        }
        if (x + 1 < n && now[x+1][y] == -1)
        {
            now[x][y] = now[x+1][y] = i;
            dfs(x, y + 1, s | (1LL << i), step + (now[x][y] != a[x][y]) + (now[x+1][y] != a[x+1][y]));
            now[x][y] = now[x+1][y] = -1;
        }
    }
}

