using namespace std;
typedef long long int ll;
typedef pair <ll, ll> ii;
typedef vector <int> vi;
typedef vector <vi> vvi; 
typedef vector <ii> vii;

vi P, rep, out, visited, original;
vvi edges, G, queries;
vector <set <int> > best;
vvi nodes;

void dfs(int v, int parent){
    ++visited[v];

    rep[v] = parent;
    best[parent].insert(-P[v]);
    nodes[parent].push_back(v);
    for (int u : G[v]){
        if (not visited[u]) dfs(u, parent);
    }
}

int findrep(int v){
    if (rep[v] == v) return v;
    else return rep[v] = findrep(rep[v]);
}

void solve(int q){
    if (q == -1) return;

    int t = queries[q][0], v = queries[q][1];
    //cout << " solving query " << q << " i.e. " << t << " and " << v << endl;

    if (t == 1){
        solve(q - 1);

        int p = findrep(v);

        if (best[p].empty()) cout << 0 << '\n';
        else {
            cout << -*best[p].begin() << '\n';
            P[original[-*best[p].begin()]] = 0;
            best[p].erase(best[p].begin());
        }
    }
    else {

        int a = edges[v][0], b = edges[v][1];

        if (best[a].size() < best[b].size()) swap(a, b);

        int pb = findrep(b), pa = findrep(a);

        if (pa < pb){
            rep[pb] = pa;

            for (int x: best[pb]){
                best[pa].insert(x);
            } 
            for (int x: nodes[pb]){
                nodes[pa].push_back(x);
            }
            best[pb].clear();
        }

        if (pa > pb){
            rep[pb] = pa;

            for (int x: best[pb]){
                best[pa].insert(x);
            } 
            for (int x: nodes[pb]){
                nodes[pa].push_back(x);
            }
            //best[pb].clear();
        }

        solve(q - 1);

        if (pa > pb){
            for (int x : nodes[pb]) {
                nodes[pa].pop_back();
                rep[x] = pb;
            }

            vi pending;


            for (int x: best[pb]){
                auto it = best[pa].find(x); 

                if (it == best[pa].end()) pending.push_back(x);
                else best[pa].erase(it);
            }

            for (int x: pending) best[pb].erase(x);
        }


        if (pa < pb){
            for (int x : nodes[pb]) {
                nodes[pa].pop_back();
                rep[x] = pb;
                if (P[x] != 0){
                    best[pa].erase(-P[x]);
                    best[pb].insert(-P[x]);
                } 
            }
        }
    }

    //cout << " finished with " << q << endl;
}


int main (){
    ios::sync_with_stdio(0);
    cin.tie(0);  

    int n, m, q;
    cin >> n >> m >> q;

    P = vi(n);
    for (int i = 0; i < n; ++i) cin >> P[i];

    rep = vi(n);
    for (int i = 0; i < n; ++i) rep[i] = i;

    edges = vvi(m, vi(2));
    out = vi(m, 0);

    for (int i = 0; i < m; ++i){
        int x, y;
        cin >> x >> y;
        --x; --y;

        edges[i] = {x, y};
    }

    queries = vvi(q, vi(2));
    for (int i = 0; i < q; ++i) {
        int t, v;
        cin >> t >> v;
        --v;

        queries[i] = {t, v};
    }

    for (int i = 0; i < q; ++i){
        if (queries[i][0] == 2) out[queries[i][1]] = 1;
    }

    G = vvi(n);

    for (int i = 0; i < m; ++i){
        if (out[i]) continue;

        G[edges[i][0]].push_back(edges[i][1]);
        G[edges[i][1]].push_back(edges[i][0]);
    }

    visited = vi(n, 0);
    best = vector <set <int> > (n);
    nodes = vvi(n);

    for (int i = 0; i < n; ++i){
        if (not visited[i]) dfs(i, i);
    }

    original = vi(n + 1);
    for (int i = 0; i < n; ++i) original[P[i]] = i;

    solve(q-1);
}

