#include <bits/stdc++.h>

#define MAX 500
#define oo  0x2f2f2f2f

using namespace std;

int cost_hp, cost_atk, cost_def, monster_hp, monster_atk, monster_def, dp[MAX][MAX][MAX];

bool wins(int hp, int atk, int def) {
	int hp_lost = monster_atk - def, monster_hp_lost = atk - monster_def;
	if(monster_hp_lost <= 0) return false;
	int n = monster_hp / monster_hp_lost + (monster_hp % monster_hp_lost != 0);
	if(hp_lost <= 0) return n > 0;
	int m = hp / hp_lost + (hp % hp_lost != 0);
	return n < m;
}

int solve(int hp, int atk, int def) {
	if(hp == MAX || atk == MAX || def == MAX) {
		return oo;
	} else if(wins(hp, atk, def)) {
		return 0;
	} else if(~dp[hp][atk][def]) {
		return dp[hp][atk][def];
	} else {
		return dp[hp][atk][def] = min(solve(hp + 1, atk, def) + cost_hp, min(solve(hp, atk + 1, def) + cost_atk, solve(hp, atk, def + 1) + cost_def));
	}
}

int main(void) {
	int hp, atk, def;
	scanf("%d %d %d", &hp, &atk, &def);
	scanf("%d %d %d", &monster_hp, &monster_atk, &monster_def);
	scanf("%d %d %d", &cost_hp, &cost_atk, &cost_def);
	memset(dp, -1, sizeof dp);
	printf("%d\n", solve(hp, atk, def));
	return 0;
}

#include<bits/stdc++.h>    
#define int long long int
