#define pb push_back
#define endl "\n"
#define F first
#define S second 
#define mod 998244353     
using namespace std;
const int mx= 200+5;
int fact[mx], invfact[mx];
int powm(int x, int y){          //Binary exponent
    int res = 1;
    x %= mod;
    while(y){
      if(y & 1)
        res *= x;
      x *= x;
      x %= mod;
      res %= mod;
      y /= 2;
    }
    return res;
}
int modinv(int num){              
   return powm(num, mod - 2);
}

void cal_fact(){                 
   fact[0] = 1;
   for(int i = 1; i < mx; i++){
      fact[i] = fact[i - 1] * i;
      fact[i] %= mod;
   }
   invfact[mx - 1] = modinv(fact[mx - 1]);
   for(int i = mx - 2; i >= 0; i--){
      invfact[i] = invfact[i + 1] * (i + 1);
      invfact[i] %= mod; 
   }
}

main()
{    
    ios_base::sync_with_stdio(false);cin.tie(0);cout.tie(0);
    cal_fact();
    int n, m, like = 0, dislike = 0;
    cin >> n >> m;
    bool type[n];
    for(int i = 0; i < n; i++)
        cin >> type[i];
    int wt[n];
    for(int i = 0; i < n; i++){
        cin >> wt[i];
        like += wt[i] * type[i];
        dislike += wt[i] * (1 - type[i]);
    }
    for(int i = 0; i < n; i++){
        int dp[m + 1][3000], ans = 0;
        memset(dp, 0, sizeof dp);
        dp[0][like] = wt[i];
        for(int j = 1; j <= m; j++){
            for(int k = 1; k <= 3000; k++){
                for(int l = 0; l <= 100; l++){
                    if(type[i]){
                        dp[j][k][l] += (((dp[j - 1][k - 1][l] * wt[i]) % mod) * invfact[k + l]) % mod;
                        dp[j][k][l] %= mod;
                        if(k > wt[i])
                            dp[j][k][l] += (((dp[j - 1][k - 1][l] * (k - wt[i])) % mod) * invfact[k + l]) % mod;
                        dp[j][k][l] %= mod;
                        dp[j][k][l] += (((dp[j - 1][k][l + 1] * l) % mod) * invfact[k + l]) % mod;
                        dp[j][k][l] %= mod;
                    }
                    else{
                        dp[j][k][l] += (((dp[j - 1][k][l + 1] * wt[i]) % mod) * invfact[k + l]) % mod;
                        dp[j][k][l] %= mod;
                        dp[j][k][l] += (((dp[j - 1][k - 1][l] * k) % mod) * invfact[k + l]) % mod;
                        dp[j][k][l] %= mod;
                        if(l > wt[i])
                            dp[j][k][l] += (((dp[j - 1][k][l + 1] * (l - wt[i])) % mod) * invfact[k + l]) % mod;
                        dp[j][k][l] %= mod;
                    }

                }
            }
        }
        for(int k = 0; k <= 100; k++){
            for(int l = 0; l <= 100; l++){
                ans += dp[m][k][l];
                ans %= mod;
            }
        }
        cout << ans << endl;
    }
}

