			ckadd(sum[x],mul(add(c2(i-1),c2(x-i)),mul(blok[i-1],mul(blok[x-i],binom(x-1,i-1)))));
			ckadd(sum[x],mul(sum[i-1],mul(mul(blok[x-i],binom(x-1,i-1)),x+1)));
			ckadd(sum[x],mul(sum[x-i],mul(mul(blok[i-1],binom(x-1,i-1)),x+1)));
		}
