/**
 * Description: modular arithmetic operations 
 * Source: 
	* KACTL
	* https://codeforces.com/blog/entry/63903
	* https://codeforces.com/contest/1261/submission/65632855 (tourist)
	* https://codeforces.com/contest/1264/submission/66344993 (ksun)
	* also see https://github.com/ecnerwala/cp-book/blob/master/src/modnum.hpp (ecnerwal)
 * Verification: 
	* https://open.kattis.com/problems/modulararithmetic
 */

template<int RT> struct mint {
	// static const int mod = MOD;
	static constexpr mint rt() { return RT; } // primitive root for FFT
	int v; explicit operator int() const { return v; } // explicit -> don't silently convert to int
	mint() { v = 0; }
	mint(ll _v) { v = int((-MOD < _v && _v < MOD) ? _v : _v % MOD);
		if (v < 0) v += MOD; }
	friend bool operator==(const mint& a, const mint& b) { 
		return a.v == b.v; }
	friend bool operator!=(const mint& a, const mint& b) { 
		return !(a == b); }
	friend bool operator<(const mint& a, const mint& b) { 
		return a.v < b.v; }
	friend void re(mint& a) { ll x; re(x); a = mint(x); }
	friend str ts(mint a) { return ts(a.v); }

	mint& operator+=(const mint& m) { 
		if ((v += m.v) >= MOD) v -= MOD; 
		return *this; }
	mint& operator-=(const mint& m) { 
		if ((v -= m.v) < 0) v += MOD; 
		return *this; }
	mint& operator*=(const mint& m) { 
		v = (ll)v*m.v%MOD; return *this; }
	mint& operator/=(const mint& m) { return (*this) *= inv(m); }
	friend mint pow(mint a, ll p) {
		mint ans = 1; assert(p >= 0);
		for (; p; p /= 2, a *= a) if (p&1) ans *= a;
		return ans; }
	friend mint inv(const mint& a) { assert(a.v != 0); 
		return pow(a,MOD-2); }

	mint operator-() const { return mint(-v); }
	mint& operator++() { return *this += 1; }
	mint& operator--() { return *this -= 1; }
	friend mint operator+(mint a, const mint& b) { return a += b; }
	friend mint operator-(mint a, const mint& b) { return a -= b; }
	friend mint operator*(mint a, const mint& b) { return a *= b; }
	friend mint operator/(mint a, const mint& b) { return a /= b; }
};

typedef mint<5> mi; // 5 is primitive root for both common mods
typedef vector<mi> vmi;
typedef pair<mi,mi> pmi;
typedef vector<pmi> vpmi;

vector<vmi> scmb; // small combinations
void genComb(int SZ) {
	scmb.assign(SZ,vmi(SZ)); scmb[0][0] = 1;
	FOR(i,1,SZ) F0R(j,i+1) 
		scmb[i][j] = scmb[i-1][j]+(j?scmb[i-1][j-1]:0);
}

mi ways[501], mad[501];
mi WAYS[501][501], MAD[501][501];
int N,M;

int main() {
	setIO(); re(N,M,MOD);
	genComb(N+5);
	ways[0] = 1;
	FOR(n,1,N+1) {
		FOR(i,1,n+1) {
			ways[n] += (n+1)*ways[i-1]*ways[n-i]*scmb[n-1][i-1];
			FOR(j,1,i+1) {
				mad[n] += (j-1)*ways[i-1]*ways[n-i]*scmb[n-1][i-1];
				mad[n] += mad[i-1]*ways[n-i]*scmb[n-1][i-1];
				mad[n] += mad[n-i]*ways[i-1]*scmb[n-1][i-1];
			}
			FOR(j,1,n-i+2) {
				mad[n] += (j-1)*ways[i-1]*ways[n-i]*scmb[n-1][i-1];
				mad[n] += mad[i-1]*ways[n-i]*scmb[n-1][i-1];
				mad[n] += mad[n-i]*ways[i-1]*scmb[n-1][i-1];
			}
		}
	}
	F0R(n,N+1) WAYS[n][0] = 1;
	FOR(n,1,N+1) FOR(m,1,n+1) {
		if (n == m) {
			WAYS[n][m] = ways[n];
			MAD[n][m] = mad[n];
		} else {
			WAYS[n][m] = WAYS[n-1][m]; 
			MAD[n][m] = MAD[n-1][m];
			FOR(l,1,m+1) {
				WAYS[n][m] += WAYS[n-l-1][m-l]*ways[l]*scmb[m][l];
				MAD[n][m] += (WAYS[n-l-1][m-l]*mad[l]+ways[l]*MAD[n-l-1][m-l])*scmb[m][l];
			}
		}
	}
	dbg(WAYS[N][M],MAD[N][M]);
	ps(MAD[N][M]);
