template <typename T> 
struct dich {
	struct item {
		item *l, *r;
		int prior, cnt;
		T key;
		item(T x, int y) {
			l = r = NULL;
			key = x;
			prior = y;
			cnt = 0;				
		}
	};

	typedef item* pitem;

	dich() {
		t = NULL;
	}

	int cnt(pitem t) {
		return (t == NULL) ? 0 : t->cnt;
	}

	void upd_cnt(pitem t) {
		if (t != NULL) {		
			t->cnt = cnt(t->l) + cnt(t->r) + 1;
		}
	}

	void split(pitem t, pitem &l, pitem &r, int key, int add = 0) {
		if (t == NULL) {
			l = r = NULL; 
			return;
		}
		int cur_key = add + cnt(t->l);
		if (key <= cur_key) {
			split(t->l, l, t->l, key, add);
			r = t;
		} else {
			split(t->r, t->r, r, key, add + 1 + cnt(t->l));
			l = t;

		}
		upd_cnt(t);
	}

	void merge(pitem &t, pitem l, pitem r) {
		if (l == NULL) t = r; else
		if (r == NULL) t = l; else
		if (l->prior > r->prior) {
			merge(l->r, l->r, r);
			t = l;
		} else {
			merge(r->l, l, r->l);
			t = r;
		}
		upd_cnt(t);	
	}

	void insert(int i, T x) {
		pitem t1 = NULL, t2 = NULL;
		split(t, t1, t2, i);
		merge(t1, t1, new item(x, rand()));
		merge(t, t1, t2);
	}

	void erase(int i) {
		pitem t1 = NULL, t2 = NULL, t3 = NULL;
		split(t, t1, t2, i);
		split(t2, t2, t3, 1);		
		merge(t, t1, t3);
	}

	T get(int i) {
		pitem t1 = NULL, t2 = NULL, t3 = NULL;
		split(t, t1, t2, i);
		split(t2, t2, t3, 1);		
		T ans = t2->key;
		merge(t2, t2, t3);
		merge(t, t1, t2);
		return ans;
	}

	void set(int i, T x) {
		pitem t1 = NULL, t2 = NULL, t3 = NULL;
		split(t, t1, t2, i);
		split(t2, t2, t3, 1);		
		t2->key = x;
		merge(t2, t2, t3);
		merge(t, t1, t2);	
	}	

	bool to_front_and_set_and_check(int i, int x) {
		pitem t1 = NULL, t2 = NULL, t3 = NULL;
		split(t, t1, t2, i);
		split(t2, t2, t3, 1);		
		if (t2->key.first != x && t2->key.first != 0) return false;
		if (t2->key.first == 0 && !can[x]) return false;		
		t2->key.first = x;		
		merge(t1, t2, t1);
		merge(t, t1, t3);
		return true;
	}
	pitem t; 
};

dich< pair<int, int> > a;

bool cmp1(pair<int, int> a, pair<int, int> b) {
	return a.second < b.second;
}

