#define M 1000000007LL
typedef long long ll;
#define all(a) a.begin() , a.end()
ll power(ll a, ll b)
{
    if(b==0)    return 1LL;
    if(b==1)    return a%M;
    if(b%2==0)  return power((a%M * a%M)%M , b/2) %M;
    return  (a%M * power((a%M * a%M)%M , (b-1)/2)%M) %M;
}
int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);    
    freopen("input.txt" , "r" , stdin);
    ll x,n;
    cin>>x>>n;
    vector<ll> pf;
    if(x%2==0)
    {
        pf.push_back(2);
        while(x%2==0)   x/=2;
    }
    for(ll i=3;i*i<=x;i+=2)
    {
        if(x%i==0)
        {
            pf.push_back(i);
            while(x%i==0)   x/=i;
        }
    }
    if(x>2) pf.push_back(x);
    sort(all(pf));
    ll ans=1,prev=0;
    for(ll kk: pf)
    {
        ll temp=kk;
        while(1)
        {
            if(n/temp < kk) break;
            else temp*=kk;
        }
        prev=0;
        while(temp>=kk)
        {
            ll pp=(n/temp)-prev;
            prev=n/temp;
            pp = power(temp%M,pp)%M;
            ans= (ans%M * pp%M ) % M;
            temp/=kk;
        }
    }
    cout<<ans<<"\n";
    return 0;
}

