	ret+=com(a,3);
	ret+=com(b,3);
	ret+=com(c,3);
	ret+=com(d,3);
	ret+=com(a,2)*(sum-a);
	ret+=com(b,2)*(sum-b);
	ret+=com(c,2)*(sum-c);
