        n1=1LL*n1*(n1-1)*(n1-2)/6%M;
        int n2=(n+1)/2;
        n2=1LL*n2*(n2-1)*(n2-2)/6%M;
        return (n1+n2)%M;
    }
    return cal(n+(d1&1)+(d2&1),0,0);
}
