        else if(j == 0)gcd[i][j] = i;
        else if(i < j)gcd[i][j] = gcd[i][j%i];
        else gcd[i][j] = gcd[i%j][j];
        dp[0][0]=cal(m,0,0);
        dp[0][1]=cal(m,0,1);
        dp[1][0]=cal(m,1,0);
