int n, m, k, p[MXN], dp[MXN], ansl, ansr;
vector<int> g[MXN], g1[MXN];

void bfs(int t){
    for(int i = 1; i <= n; i++){
        dp[i] = 1e9;
    }
    dp[t] = 0;
    deque<int> q;
    q.push_back(t);
    while(!q.empty()){
        int x = q.front();
        q.pop_front();
        for(int i = 0; i < g[x].size(); i++){
            int to = g[x][i];
            if(dp[x] + 1 <= dp[to]){
                dp[to] = dp[x] + 1;
                q.push_back(to);
            }
        }
    }
}

