int main() {
    //ios::sync_with_stdio(false);
    //cin.tie(0);
    //cout.tie(0);
    ll l, r, x, y;
    cin >> l >> r >> x >> y;
    int cnt = 0;
    ll root = sqrtl(x)+1;
    vector<ll> factors;
    for(ll i = 2; i <= root; ++i) {
        if(x%i == 0) {
            factors.push_back(i);
            factors.push_back(x/i);
        }
    }
    if(x > 1) factors.push_back(x);
    root = sqrtl(y)+1;
    for(ll i = 2; i <= root; ++i) {
        if(y%i == 0) {
            vector<ll> temp;
            temp.push_back(i);
            temp.push_back(y/i);
            for(ll f : factors) {
                if((long double)f*i <= x*y)
                temp.push_back(f*i);
                if((long double)f*i <= x*y)
                temp.push_back(f*y/i);
            }
            for(auto &f : temp) factors.push_back(f);
        }
    }
    if(y > 1) {
        vector<ll> temp;
        for(ll f : factors) {
            if((long double)f*y <= x*y)
            temp.push_back(f*y);
        }
        for(auto &f : temp) factors.push_back(f);
    }
    factors.push_back(1);
    factors.push_back(x*y);
    sort(factors.begin(), factors.end());
    factors.erase(unique(factors.begin(), factors.end()), factors.end());
    for(ll f1 : factors) {
        ll f2 = x*y/f1;
        if(__gcd(f1,f2) == x && f1*f2/__gcd(f1,f2) == y && f1 <= f2 && f1 >= l && f1 <= r && f2 >= l && f2 <= r) {
            cnt += 2;
            if(f1 == f2) {
                cnt--;
            }
        }
    }
    cout << cnt << '\n';
    return 0;
}

