bool tri(int a,int b,int c){
	if(a+b<=c) return false;
	if(b+c<=a) return false;
	if(a+c<=b) return false;
	return true;
}
int main(){
	int i,j;
	while(scanf("%d",&n)!=EOF){
		int l=0;
		for(i=1;i<n;i++){
			for(j=i+1;j<=n;j++){
				a[l].a=i;
				a[l].b=j;
				a[l++].w=(i^j);
			}
		}
		int ans=0;
		for(i=0;i<l;i++){
			for(j=a[i].b+1;j<=n;j++){
				if(a[i].a==j || a[i].b==j) continue;
				if((a[i].w^j)==0){
					if(tri(a[i].a,a[i].b,j)) ans++;
				}
			}
		}
		printf("%d\n",ans);
	}
}

