#include<bits/stdc++.h>
#pragma GCC optimize("Ofast")
using namespace std;
#define ll long long
#define PII pair<int,int>
const ll N=50 , INF=1e9 , mod=998244353 , Z=180;

struct Matrix
{	ll M[Z][Z];
	
	ll* operator[](int i) {	  return M[i];	}

	Matrix operator *(const Matrix& B)
	{	static Matrix T1;
		memset(T1.M,0,sizeof T1.M);
		for(int i=0;i<Z;i++)
		for(int j=0;j<Z;j++)
		for(int k=0;k<Z;k++)
		T1[i][j]+=M[i][k]*B[k][j],T1[i][j]%=mod;
		return T1;
	}
	
	
};
	Matrix fmi(Matrix& G, ll pow)
	{	
		static Matrix T2;
		memset(T2.M,0,sizeof T2.M);
		for(int i=0;i<Z;i++) T2[i][i]=1;
		
		while(pow)
		{	if(pow&1) T2=T2*G;
			G=G*G;
			pow>>=1;
		}
		return T2;
	}
	
Matrix M0,M1,res;

struct Trie
{	
	int ch[N][26];
	bool ed[N];
	int idx=0;
	
	void insert(char s[])
	{	int p=0;
		for(int i=0;s[i];i++)
		{	int c=s[i]-'a';
			if(!ch[p][c]) ch[p][c]=++idx;
			p=ch[p][c];
		}
		ed[p]=1;
	}
	
	int mp[3000],idn;
	bool ED[3000];
	
	int dfs(int u,int v)
	{	if(mp[u*N+v]) return mp[u*N+v];
		
		mp[u*N+v]=++idn;
		ED[idn]=(ed[u]&&ed[v]);
		int x=idn , su , sv ;
		
		for(int i=0;i<26;i++)
		{	
			if(ch[u][i])
			{	
				if(ch[v][i])
				{	su=ch[u][i] , sv=ch[v][i];
					M1[x][dfs(su,sv)]++;
				}
				if(ed[v]&&ch[0][i])
				{	su=ch[u][i] , sv=ch[0][i];
					M1[x][dfs(su,sv)]++;
				}
			}
			
			if(ed[u]&&ch[0][i])
			{	if(ch[v][i])
				{	su=ch[0][i] , sv=ch[v][i];
					M1[x][dfs(su,sv)]++;
				}
				if(ed[v]&&ch[0][i])
				{	su=ch[0][i] , sv=ch[0][i];
					M1[x][dfs(su,sv)]++;
				}
			}
		}
		return mp[u*N+v];
	}
	
	void solve(int m)
	{	idn=0;
		memset(mp,0,sizeof mp);
		memset(ED,0,sizeof ED);
		
		for(int c=0;c<26;c++) if(ch[0][c]) dfs(ch[0][c],ch[0][c]); 
		
		for(int c=0;c<26;c++) if(ch[0][c]) M0[0][mp[ch[0][c]*N+ch[0][c]]]++;
		res=M0*fmi(M1,m-1);
		ll ans=0;
		for(int i=1;i<=idn;i++) if(ED[i]) ans=(ans+res[0][i])%mod;
		printf("%lld",ans);
	}
	
}trie;

char s[10];
signed main()
{	int n,m;scanf("%d %d",&n,&m);
	for(int i=1;i<=n;i++) scanf("%s",s),trie.insert(s);
	trie.solve(m);
    return 0;
}

