#define int long long
#define pa pair<int ,int >
#define mp make_pair 
using namespace std;
inline int read(){int x=0,f=1,ch=getchar(); while(ch<'0'||ch>'9'){if(ch=='-') f=-1; ch=getchar();} while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();} return x*f;}
inline void write(int x){if (x<0) putchar('-'),x=-x; if (x>=10) write(x/10); putchar(x%10+'0');}
inline void writeln(int x){write(x); puts("");}
const int N=45,M=161,mod=998244353;
int root,n,m,cnt,End[N],tran[N][26];
char s[N];
inline void insert(){

	int now=root,len=strlen(s+1);
	for (int i=1;i<=len;i++){
		int c=s[i]-'a';
		if (tran[now][c]==-1) tran[now][c]=++cnt;
		 now=tran[now][c];
	}
	End[now]|=1;
}
inline void init(){
	memset(tran,-1,sizeof tran);
	n=read(); m=read(); root=0;
	for (int i=1;i<=n;i++){
		scanf("%s",s+1);
		insert();
	}
}
struct matrix{
	int mat[M][M];	
	matrix() {memset(mat,0,sizeof(mat));}	
	matrix operator * (const matrix &C) const
	{
		matrix ans;
		for(int i=0;i<M;i++)
			for(int j=0;j<M;j++)
				for(int k=0;k<M;k++)
					ans.mat[i][j]=(ans.mat[i][j]+mat[i][k]*C.mat[k][j]%mod)%mod;
		return ans;
	}
}A;
pa q[M];
int h,t;
map<pa,int >id;
int get(pa p){
	if (p.first>p.second) swap(p.first,p.second);
	if (!id.count(p)){
		int k=id.size();
		id[p]=k;
		q[++t]=p;
	}
	return id[p];
}
inline matrix pow(matrix B,int k){
	matrix C;
	for (int i=0;i<M;i++) C.mat[i][i]=1;
	for (;k;k>>=1,B=B*B){
		if (k&1) C=C*B;
	}
	return C;
}
inline void solve(){
	id[mp(0,0)]=0; q[++t]=(pa){0,0};
	while (h<t){
		pa u=q[++h];
		int x=get(u);
		for (int c=0;c<26;c++){
			pa nxt=mp(tran[u.first][c],tran[u.second][c]);
			if (nxt.first==-1||nxt.second==-1) continue;
			A.mat[x][get(nxt)]++;
			if (End[tran[u.first][c]]) A.mat[x][get(mp(0,nxt.second))]++;
			if (End[tran[u.second][c]]) A.mat[x][get(mp(nxt.first,0))]++;
			if (End[tran[u.first][c]]&&End[tran[u.second][c]]) A.mat[x][0]++;
		}
	}
	writeln(pow(A,m).mat[0][0]);
} 
signed main(){
	init(); solve();
	return 0;
}

