template<class Fun>
class y_combinator_result {
  Fun fun_;
public:
  template<class T>
  explicit y_combinator_result(T &&fun): fun_(std::forward<T>(fun)) {}
 
  template<class ...Args>
  decltype(auto) operator()(Args &&...args) {
    return fun_(std::ref(*this), std::forward<Args>(args)...);
  }
};
 
template<class Fun>
decltype(auto) make_fun(Fun &&fun) {
  return y_combinator_result<std::decay_t<Fun>>(std::forward<Fun>(fun));
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  int n, m;
  cin >> n >> m;
  struct Node {
    char c;
    int cnt;
    vector<Node*> sons;
    Node(char _c = 0, char _cnt = 0): c(_c), cnt(_cnt) {
      sons.clear();
    }
  };
  Node* root = new Node();
  auto insert = make_fun([&](auto insert, Node* cur, const string& s, int depth) -> void {
    if (depth == (int)s.size()) {
      cur->cnt += 1;
      return;
    }
    for (auto son: cur->sons) {
      if (son->c == s[depth]) {
        insert(son, s, depth + 1);
        return;
      }
    }
    cur->sons.push_back(new Node(s[depth], 0));
    insert(cur->sons.back(), s, depth + 1);
  });
  for (int i = 0; i < n; i++) {
    string s;
    cin >> s;
    insert(root, s, 0);
  }
  map<Node*, string> path;
  auto dfs = make_fun([&](auto dfs, Node* cur) -> void {
    for (auto son: cur->sons) {
      if (cur == root) {
        path[son] = son->c;
      } else {
        path[son] = path[cur] + son->c;
      }
      dfs(son);
    }
  });
  dfs(root);
  vector<pair<Node*, Node*>> states;
  for (auto t: path) {
    states.push_back(make_pair(t.first, t.first));
  }
  for (auto t1: path) {
    string s1 = t1.second;
