		for (int i = half; i < size; i++) for (int j = 0; j < half; j++) A21[i - half][j] = A[i][j];
		for (int i = half; i < size; i++) for (int j = half; j < size; j++) A22[i - half][j - half] = A[i][j];
		for (int i = 0; i < half; i++) for (int j = 0; j < half; j++) B11[i][j] = B[i][j];
		for (int i = 0; i < half; i++) for (int j = half; j < size; j++) B12[i][j - half] = B[i][j];
		for (int i = half; i < size; i++) for (int j = 0; j < half; j++) B21[i - half][j] = B[i][j];
		for (int i = half; i < size; i++) for (int j = half; j < size; j++) B22[i - half][j - half] = B[i][j];

		M1 = (A11 + A22) * (B11 + B22);
		M2 = (A21 + A22) * B11;
		M3 = A11 * (B12 - B22);
		M4 = A22 * (B21 - B11);
		M5 = (A11 + A12) * B22;
		M6 = (A21 - A11) * (B11 + B12);
		M7 = (A12 - A22) * (B21 + B22);

		C11 = M1 + M4 - M5 + M7;
		C12 = M3 + M5;
		C21 = M2 + M4;
		C22 = M1 - M2 + M3 + M6;

		Matrix ans(size);
		for (int i = 0; i < half; i++) for (int j = 0; j < half; j++) ans[i][j] = C11[i][j];
		for (int i = 0; i < half; i++) for (int j = half; j < size; j++) ans[i][j] = C12[i][j - half];
		for (int i = half; i < size; i++) for (int j = 0; j < half; j++) ans[i][j] = C21[i - half][j];
		for (int i = half; i < size; i++) for (int j = half; j < size; j++) ans[i][j] = C22[i - half][j - half];

		A11.del(); A12.del(); A21.del(); A22.del();
		B11.del(); B12.del(); B21.del(); B22.del();
		C11.del(); C12.del(); C21.del(); C22.del();
		M1.del(); M2.del(); M3.del(); M4.del(); M5.del(); M6.del(); M7.del();
		return ans;
	}

	Matrix operator + (const Matrix &x) const {
		Matrix ans(_size);
		for (int i = 0; i < _size; i++) for (int j = 0; j < _size; j++) ans[i][j] = val[i][j] + x[i][j];
		return ans;
	}
	Matrix operator - (const Matrix &x) const {
		Matrix ans(_size);
		for (int i = 0; i < _size; i++) for (int j = 0; j < _size; j++) ans[i][j] = val[i][j] - x[i][j];
		return ans;
	}
	Matrix operator * (const T &x) const {
		Matrix ans(_size);
		for (int i = 0; i < _size; i++) for (int j = 0; j < _size; j++) ans[i][j] = val[i][j] * x;
		return ans;
	} 
	Matrix operator * (const Matrix &x) const {
		if (_size < kMultiplicationThreshold) return Naive_Multiplication(*this, x);
		else return DC_Multiplication(*this, x);
	} 
	Matrix operator / (const T &x) const {
		Matrix ans(_size);
		for (int i = 0; i < _size; i++) for (int j = 0; j < _size; j++) ans[i][j] = val[i][j] * x;
		return ans;
	}
	Matrix operator += (const Matrix &x) {return *this = *this + x;} 
	Matrix operator -= (const Matrix &x) {return *this = *this - x;} 
	Matrix operator *= (const T &x) {return *this = *this * x;} 
	Matrix operator *= (const Matrix &x) {return *this = *this * x;} 
	Matrix operator /= (const T &x) {return *this = *this / x;} 

	void out() const {
		for (int i = 0; i < _size; i++, printf("\n")) for (int j = 0; j < _size; j++) printf("%5d", val[i][j]);
		return ;
	}
};

template <typename T1, typename T2> Matrix<T1> Pow(Matrix<T1> A, T2 b) {
	Matrix<T1> ans(A.size(), 1);
	for (; b; b >>= 1, A *= A) if (b & 1) ans *= A;
	return ans;
}
