            res[i][j] = 0;
            if(a[i][k])
                for(int k = 0; k < N; k++) {
                    res[i][j] = (1LL * a[i][k] * b[k][j] + res[i][j]) % mod;
                }
        }
    for(int i = 0; i < N; i++)
        for(int j = 0; j < N; j++)
            a[i][j] = res[i][j];
}
void power(int temp[][N], int p) {

    int res[N][N] = {0};
    for(int i = 0; i < N; i++)
        res[i][i] = 1;

    while(p) {
        if(p & 1)
            mul(res, temp);
        mul(temp, temp);
        p >>= 1;
    }

    for(int i = 0; i < N; i++)
        for(int j = 0; j < N; j++)
            temp[i][j] = res[i][j];
}
