    cout << ',';
    __print(x.second);
    cout << "}";
}
template <typename T, typename V, typename U>
void __print(const tuple<T, V, U> &x)
{
    cout << '{';
    __print(get<0>(x));
    cout << ',';
    __print(get<1>(x));
    cout << ',' << get<2>(x);
    cout << "}";
}
template <typename T>
void __print(const vector<T> &x)
{
    cout << "{";
    for (int u69 = 0; u69 < x.size(); u69++)
    {
        __print(x[u69]);
        cout << (u69 + 1 == x.size() ? "" : ",");
    };
    cout << "}";
}
template <typename T>
void __print(const T &x)
{
    int f = 0;
    cout << '{';
    for (auto &i : x)
        cout << (f++ ? "," : ""), __print(i);
    cout << "}";
}
void _print() { cout << "]\n"; }
template <typename T, typename... V>
void _print(T t, V... v)
{
    __print(t);
    if (sizeof...(v))
        cout << ", ";
    _print(v...);
}
