bool dfs(int i, int j, int cnt, int n, int m, vector<vector<bool>> &visi, vector<vector<ll>> &mtx)
{
    if (i == n - 1 and j == m - 1)
    {
        debug(cnt);
        return (cnt == ((n + m - 1) - cnt));
    }

    visi[i][j] = true;
    int dir[5] = {0, 1, 0, -1, 0};

    for (int k = 0; k < 4; k++)
    {
        int new_i = i + dir[k];
        int new_j = j + dir[k + 1];

        if (new_i >= 0 and new_i < n and new_j >= 0 and new_j < m and !visi[new_i][new_j])
        {
            int p = (mtx[new_i][new_j] == 1) ? 1 : 0;
            if (dfs(new_i, new_j, cnt + p, n, m, visi, mtx))
            {
                return true;
            }
        }
    }

    return false;
}

bool isSmaller(string str1, string str2)
{
    int n1 = str1.length(), n2 = str2.length();

    if (n1 < n2)
        return true;
    if (n2 < n1)
        return false;

    for (int i = 0; i < n1; i++)
        if (str1[i] < str2[i])
            return true;
        else if (str1[i] > str2[i])
            return false;

    return false;
}

string findDiff(string str1, string str2)
{
    if (isSmaller(str1, str2))
        swap(str1, str2);

    string str = "";

    int n1 = str1.length(), n2 = str2.length();

    reverse(str1.begin(), str1.end());
    reverse(str2.begin(), str2.end());

    int carry = 0;

    for (int i = 0; i < n2; i++)
    {

        int sub = ((str1[i] - '0') - (str2[i] - '0') - carry);

        if (sub < 0)
        {
            sub = sub + 10;
            carry = 1;
        }
        else
            carry = 0;

        str.push_back(sub + '0');
    }

    for (int i = n2; i < n1; i++)
    {
        int sub = ((str1[i] - '0') - carry);

        if (sub < 0)
        {
            sub = sub + 10;
            carry = 1;
        }
        else
            carry = 0;

        str.push_back(sub + '0');
    }
    reverse(str.begin(), str.end());

    return str;
}

