void update(vector<ll> &fenwick, ll i, ll val)
{
    while (i < (int)fenwick.size())
    {
        fenwick[i] += val;
        i += (i & (-i));
    }
}
ll sum(vector<ll> &fenwick, ll i)
{
    ll sm = 0;
    while (i > 0)
    {
        sm += (fenwick[i]);
        i -= (i & (-i));
    }
    return sm;
}
ll count_primes(ll a)
{
    ll cnt = 0;
    while ((a % 2) == 0)
    {
        cnt += 1;
        a /= 2;
    }
    for (int i = 3; i * i <= n; i += 2)
    {
        while ((a % i) == 0)
        {
            a /= i;
            cnt += 1;
        }
    }
    if (a > 1)
    {
        cnt += 1;
    }
    return cnt;
}
ll high_prime_factor(ll n)
{
    ll ans = 1;
    for (ll i = 1; i * i <= n; i += 1)
    {
        if (prime[i])
        {
            if (n % i == 0)
            {
                ans = max({ans, i, n / i});
            }
        }
    }

    return ans;
}
