void recur(int i, int j, int cnt, vector<vector<int>> &visi, vector<vector<int>> &matx)
{
    if (cnt == 64)
    {
        cout << "her"
             << "\n";
        return;
    }

    for (int k = 0; k < 8; k++)
    {
        int new_x = i + dir[k][0];
        int new_y = j + dir[k][1];
        if (isvalid(new_x, new_y, visi))
        {
            // cout<<new_x<<" "<<new_y<<"\n";
            visi[new_x][new_y] = 1;
            matx[new_x][new_y] = cnt;
            cnt += 1;
            recur(new_x, new_y, cnt + 1, visi, matx);
            cnt -= 1;
            visi[new_x][new_y] = 0;
            matx[new_x][new_y] = -1;
        }
    }
    return;
}

