#include <bits/stdc++.h>
using namespace std;

const int maxn = 1010, sigma_size = 20;
int f[1 << sigma_size];
unordered_map<int, tuple<int, int, int>> dp[maxn];
int cnt[maxn][sigma_size];
char s[maxn];

int main() {
    int n;
    scanf("%d", &n);
    scanf("%s", s + 1);
    int st = 0, tot = 0;
    f[0] = n + 1;
    for (int i = n; i; i--)
        if (!((st >> (s[i] - 'a')) & 1))
        {
            st |= 1 << (s[i] - 'a'), tot++;
            for (int j = 0; j < 1 << sigma_size; j++)
                if (!f[j] && ((j & st) == j))
                    f[j] = i;
        }
    for (int i = 0; i < st; i++)
        if (f[i])
            printf("f[%d] = %d\n", i, f[i]);
    for (int i = 1; i <= n; i++)
        for (int j = 0; j < sigma_size; j++) cnt[i][j] = cnt[i - 1][j] + (s[i] - 'a' == j);
    int nxt, val;
    dp[0][0] = make_tuple(0, 0, 0);
    for (int i = 0; i <= n; i++)
        for (auto j : dp[i]) {
            for (int k = 0; k < sigma_size; k++)
                if (((st >> k) & 1) && !((j.first >> k) & 1)) {
                    nxt = j.first << k, val = cnt[f[st ^ nxt] - 1][k] - cnt[i][k];
                    if (!dp[f[st ^ nxt]].contains(nxt) || get<2>(dp[f[st ^ nxt]][nxt]) < val)
                        dp[f[st ^ nxt]][nxt] = make_tuple(i, j.first, val);
                }
        }
    int cur;
    string ans = "", tmp;
    for (int i = 1; i <= n; i++)
        if (dp[i].contains(st)) {
            auto u = dp[i][st];
            tmp.clear(), cur = tot - 1;
            while (get<2>(u)) {
                for (int j = 1; j <= get<2>(u); j++) tmp.push_back(cur + 'a');
                cur--, u = dp[get<0>(u)][get<1>(u)];
            }
            if (ans < tmp)
                ans = tmp;
        }
    cout << ans << endl;
    return 0;
}
