a = Array.new(123, gets.split[1].to_i)
gets.chars{|c| a[c.ord] -= 1}
p a.min < 0 ? 'NO' : 'YES'

n,m,k=gets.split.map(&:to_i)
planets=n.times.map{
	gets
	m.times.map{gets.split.map(&:to_i)}
}
ans=0
planets.permutation(2).each{|p1,p2|
	t=[]
	m.times{|i|t<<[p2[i][1]-p1[i][0],p1[i][2]]}
	kk=k
	a=0
	t.sort_by{|d,c|d}.reverse.each{|d,c|
		num=[kk,c].min
		a+=num*d
		kk-=num
		break if kk==0
	}
	ans=a if a>ans
}
p ans
